// Enhanced topic classification and routing
const topics = $json.results;
const classifiedTopics = [];

for (const topic of topics) {
  const topicData = {
    id: topic.id,
    title: topic.properties.Topic?.title?.[0]?.plain_text || 'Untitled',
    pillar: topic.properties.Pillar?.select?.name || 'General',
    contentType: topic.properties['Content Type']?.select?.name || 'LinkedIn Post',
    priority: topic.properties.Priority?.select?.name || 'Medium',
    targetPlatforms: topic.properties['Target Platforms']?.multi_select?.map(p => p.name) || ['LinkedIn'],
    deadline: topic.properties.Deadline?.date?.start,
    keywords: topic.properties.Keywords?.rich_text?.[0]?.plain_text?.split(',').map(k => k.trim()) || [],
    aiComplexity: topic.properties['AI Complexity']?.select?.name || 'Standard',
    visualRequired: topic.properties['Visual Required']?.checkbox || true,
    emergencyFlag: topic.properties.Status?.select?.name === 'Emergency'
  };

  // Classify content type for routing
  topicData.workflowType = determineWorkflowType(topicData);

  classifiedTopics.push(topicData);
}

function determineWorkflowType(topic) {
  if (topic.emergencyFlag) return 'emergency';
  if (topic.contentType === 'Product Teardown') return 'teardown';
  if (topic.contentType === 'Thread') return 'thread';
  if (topic.contentType === 'Carousel') return 'carousel';
  if (topic.pillar === 'Internal Learnings') return 'internal';
  return 'standard';
}

return classifiedTopics.map(topic => ({ json: topic }));

// Advanced research compilation with error handling
try {
  const allInputs = $input.all();
  const topicData = allInputs.find(item => item.json.title) || { json: { title: 'Unknown', keywords: [] } };

  // Initialize research block
  const researchBlock = {
    topic: topicData.json.title,
    pillar: topicData.json.pillar || 'General',
    contentType: topicData.json.contentType || 'LinkedIn Post',
    keywords: topicData.json.keywords || [],
    aiComplexity: topicData.json.aiComplexity || 'Standard',
    timestamp: new Date().toISOString(),
    sources: {
      youtube: { count: 0, data: [] },
      twitter: { count: 0, data: [] },
      forums: { count: 0, data: [] },
      product: { count: 0, data: [] }
    },
    insights: {
      trending_topics: [],
      key_pain_points: [],
      popular_solutions: [],
      engagement_patterns: []
    }
  };

  // Process YouTube data
  const youtubeInput = allInputs.find(item => item.json.source === 'youtube' || item.json.videos);
  if (youtubeInput?.json?.videos?.length > 0) {
    researchBlock.sources.youtube.count = youtubeInput.json.videos.length;
    researchBlock.sources.youtube.data = youtubeInput.json.videos.slice(0, 15).map(video => ({
      title: video.title || 'No title',
      views: video.views || 0,
      engagement: video.engagement || 0,
      channel: video.channel || 'Unknown',
      summary: video.description?.substring(0, 200) || ''
    }));
  }

  // Process Twitter data  
  const twitterInput = allInputs.find(item => item.json.source === 'twitter' || item.json.posts);
  if (twitterInput?.json?.posts?.length > 0) {
    researchBlock.sources.twitter.count = twitterInput.json.posts.length;
    researchBlock.sources.twitter.data = twitterInput.json.posts.slice(0, 20).map(post => ({
      text: post.text || 'No content',
      likes: post.likes || 0,
      retweets: post.retweets || 0,
      author: post.author || 'Unknown',
      engagement_rate: ((post.likes || 0) + (post.retweets || 0)) / Math.max(1, post.followers || 1000)
    }));
  }

  // Process Forum data
  const forumInput = allInputs.find(item => item.json.items);
  if (forumInput?.json?.items?.length > 0) {
    researchBlock.sources.forums.count = forumInput.json.items.length;
    researchBlock.sources.forums.data = forumInput.json.items.slice(0, 10).map(item => ({
      title: item.title || 'No title',
      snippet: item.snippet || 'No content',
      link: item.link || '',
      source: item.displayLink || 'Unknown'
    }));
  }

  // Generate insights
  const allText = [
    ...researchBlock.sources.youtube.data.map(v => v.title + ' ' + v.summary),
    ...researchBlock.sources.twitter.data.map(p => p.text),
    ...researchBlock.sources.forums.data.map(f => f.title + ' ' + f.snippet)
  ].join(' ').toLowerCase();

  // Extract trending topics (simple keyword frequency)
  const words = allText.split(/\s+/).filter(word => word.length > 4);
  const wordFreq = {};
  words.forEach(word => wordFreq[word] = (wordFreq[word] || 0) + 1);
  researchBlock.insights.trending_topics = Object.entries(wordFreq)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 10)
    .map(([word, freq]) => ({ term: word, frequency: freq }));

  // Create comprehensive text summary for LLMs
  let textSummary = `CONTENT ANALYSIS FOR: ${researchBlock.topic}\n`;
  textSummary += `PILLAR: ${researchBlock.pillar}\n`;
  textSummary += `COMPLEXITY: ${researchBlock.aiComplexity}\n\n`;

  if (researchBlock.sources.youtube.count > 0) {
    textSummary += "=== YOUTUBE INSIGHTS ===\n";
    researchBlock.sources.youtube.data.forEach((video, i) => {
      textSummary += `${i+1}. ${video.title} (${video.views} views, ${video.engagement} engagement)\n`;
      if (video.summary) textSummary += `   Summary: ${video.summary}\n`;
    });
    textSummary += "\n";
  }

  if (researchBlock.sources.twitter.count > 0) {
    textSummary += "=== TWITTER/X INSIGHTS ===\n";
    researchBlock.sources.twitter.data.forEach((post, i) => {
      textSummary += `${i+1}. ${post.text} (${post.likes} likes, ${post.retweets} RTs)\n`;
    });
    textSummary += "\n";
  }

  if (researchBlock.sources.forums.count > 0) {
    textSummary += "=== COMMUNITY DISCUSSIONS ===\n";
    researchBlock.sources.forums.data.forEach((forum, i) => {
      textSummary += `${i+1}. ${forum.title}\n   ${forum.snippet}\n   Source: ${forum.source}\n\n`;
    });
  }

  textSummary += "=== TRENDING TOPICS ===\n";
  researchBlock.insights.trending_topics.forEach(topic => {
    textSummary += `- ${topic.term} (mentioned ${topic.frequency} times)\n`;
  });

  return {
    json: {
      research_block: researchBlock,
      research_text: textSummary,
      topic_data: topicData.json,
      processing_status: 'success',
      data_quality: calculateDataQuality(researchBlock)
    }
  };

} catch (error) {
  return {
    json: {
      research_block: null,
      research_text: 'Error compiling research data',
      topic_data: $input.first().json,
      processing_status: 'error',
      error_message: error.message,
      data_quality: 0
    }
  };
}

function calculateDataQuality(block) {
  let score = 0;
  if (block.sources.youtube.count > 0) score += 30;
  if (block.sources.twitter.count > 0) score += 30;
  if (block.sources.forums.count > 0) score += 20;
  if (block.insights.trending_topics.length > 0) score += 20;
  return Math.min(100, score);
}

