// Fallback content generation for low-quality research
const topicData = $json.topic_data || {};
const fallbackContent = {
  title: `Quick Insight: ${topicData.title || 'BuildLabs Update'}`,
  content: `Based on our research into ${topicData.title || 'this topic'}, here are key insights for solo founders:\n\n‚Ä¢ Research quality was below threshold (${$json.data_quality}%)\n‚Ä¢ Manual review and additional research recommended\n‚Ä¢ Consider alternative topic or deeper research\n\nThis is a fallback post generated when automated research doesn't meet our quality standards. We prioritize value over volume at BuildLabs.\n\nWhat topics would you like us to dive deeper into?`,
  platforms: ['LinkedIn'],
  requiresManualReview: true,
  isFallback: true
};

return { json: fallbackContent };
